var toggle=document.querySelector(".menu-toggle"),inner=document.querySelector(".menu-inner"),body=document.querySelector("body");toggle.addEventListener("click",function(e){e.preventDefault(),inner.classList.contains("menu-inner-show")?(body.classList.remove("menu-opened"),inner.classList.remove("menu-inner-show")):(body.classList.add("menu-opened"),inner.classList.add("menu-inner-show"))}),window.addEventListener("keydown",function(e){27==e.keyCode&&inner.classList.contains("menu-inner-show")&&inner.classList.remove("menu-inner-show")});