var toggle=document.querySelector(".menu__toggle"),inner=document.querySelector(".menu__inner"),body=document.querySelector("body");toggle.addEventListener("click",function(e){e.preventDefault(),inner.classList.contains("menu__inner_state_show")?(body.classList.remove("menu-opened"),inner.classList.remove("menu__inner_state_show")):(body.classList.add("menu-opened"),inner.classList.add("menu__inner_state_show"))}),window.addEventListener("keydown",function(e){27==e.keyCode&&inner.classList.contains("menu__inner_state_show")&&inner.classList.remove("menu__inner_state_show")});